mapper.select:
select
	exc_num, ex_num, exc_writer, exc_contents, exc_date
from exc_comment
	where ex_num = #{ex_num}
	order by exc_num desc limit #{displayPost}, #{postNum}

mapper.update:
update exc_comment set
	exc_contents = #{exc_contents}
where exc_num = #{exc_num}
	and ex_num = #{ex_num}

mapper.select:
select count(exc_num)
from exc_comment
where ex_num = #{ex_num}

select
	exc_num, ex_num, exc_writer, exc_contents, exc_date
from exc_comment
	where ex_num = #{ex_num}
	and exc_num = #{exc_num}
